$(document).ready(function(){$(function(){$("form[name='reset_password']").validate({rules:{"user[password]":{required:!0,minlength:8},"user[password_confirmation]":{required:!0,equalTo:"#ResetNewPassword"},messages:{"user[password]":{required:"Please provide a password",minlength:"Password is too short (minimum is 8 characters)"},"user[password_confirmation]":{equalTo:"Please enter same password",required:"Please enter confirm password"}}},submitHandler:function(e,s){ajaxRequest(s)}})}),$("#resetEyeNP").click(function(){const e=$(".toggle-password-new-pass");"password"===e.attr("type")?e.attr("type","text"):e.attr("type","password")}),$("#resetEyeCP").click(function(){const e=$(".toggle-password-confirm-pass");"password"===e.attr("type")?e.attr("type","text"):e.attr("type","password")})});